SELECT a.discipline, AVG(a.access_duration) as avg_access_duration 
FROM temp_achive_lp_sapling_combined_activations_ai a
GROUP BY a.discipline;

SELECT SUM(a.us_net_price) as total_net_price 
FROM temp_achive_lp_sapling_combined_activations_ai a 
WHERE LOWER(a.paid_unpaid) = 'paid' AND a.year = EXTRACT(YEAR FROM CURRENT_DATE);
