SELECT COUNT (DISTINCT a.unique_id) AS total_enrollments 
FROM temp_achive_lp_sapling_combined_activations_ai a 
WHERE a.year = 2020;

SELECT AVG(a.us_net_price) AS avg_net_price 
FROM temp_achive_lp_sapling_combined_activations_ai a 
WHERE a.year = 2020 AND a.discipline ILIKE '%Physics%';

SELECT AVG(a.access_duration) AS avg_duration 
FROM temp_achive_lp_sapling_combined_activations_ai a 
WHERE a.year = 2019;

SELECT COUNT(a.unique_id) AS total_enrollments 
FROM temp_achive_lp_sapling_combined_activations_ai a 
WHERE a.year = 2020 AND a.has_used_access_code = 'Yes';
